mapper.select:
SELECT library_idx, library_title,library_cover,library_author,library_publisher,library_sharestate,library_use FROM library 


		member_idx=#{param1} AND library_blind=0 AND library_sharestate=0 ORDER BY library_reg_date DESC LIMIT 9 OFFSET #{param2}


		member_idx=#{param1} AND library_blind=0 AND library_use IN('대여교환', '대여') ORDER BY library_reg_date DESC LIMIT 9 OFFSET #{param2}


		member_idx=#{param1} AND library_blind=0 AND library_use IN('대여교환', '교환') ORDER BY library_reg_date DESC LIMIT 9 OFFSET #{param2}


		member_idx=#{param1} AND library_blind=0 AND library_use='소장' ORDER BY library_reg_date DESC LIMIT 9 OFFSET #{param2}


		member_idx=#{param1} AND library_blind=0 AND library_use='위시' ORDER BY library_reg_date DESC LIMIT 9 OFFSET #{param2}
